CREATE SCHEMA proyecto_integrador;

USE proyecto_integrador;

-- creación tabla de usuarios
CREATE TABLE usuarios (
/* nomnbreColumna TipoDeDato Restricciones*/
id_usuario INT UNSIGNED PRIMARY KEY AUTO_INCREMENT,
email VARCHAR(100) NOT NULL,
contrasenia VARCHAR(100) NOT NULL,
fecha DATE NOT NULL,
dni INT UNIQUE NOT NULL,
foto_texto VARCHAR(100) NULL,
created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
deleted_at TIMESTAMP NULL ON UPDATE CURRENT_TIMESTAMP
);

-- creación tabla de productos
CREATE TABLE productos (
id_producto INT UNSIGNED PRIMARY KEY AUTO_INCREMENT,
foto_texto VARCHAR(100) NOT NULL,
nombre VARCHAR(100) NOT NULL,
descripcion VARCHAR(300) NOT NULL, 
created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
deleted_at TIMESTAMP NULL ON UPDATE CURRENT_TIMESTAMP,
id_usuario INT UNSIGNED,

FOREIGN KEY (id_usuario) REFERENCES usuarios(id_usuario)
);

-- creación tabla de comentarios
CREATE TABLE comentarios(
id_comentario INT UNSIGNED PRIMARY KEY AUTO_INCREMENT,
id_producto INT UNSIGNED,
id_usuario INT UNSIGNED,
texto VARCHAR(500) NOT NULL,
created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
deleted_at TIMESTAMP NULL ON UPDATE CURRENT_TIMESTAMP,

FOREIGN KEY (id_usuario) REFERENCES usuarios (id_usuario),
FOREIGN KEY (id_producto) REFERENCES productos (id_producto)
);

ALTER TABLE usuarios
MODIFY foto_texto VARCHAR(10000);

ALTER TABLE productos
MODIFY foto_texto VARCHAR(10000);


